// I import fetch for API requests
const fetch = require('node-fetch');

// ----------------Configuration----------------
const GHL_ACCESS_TOKEN = 'pit-94f29fed-e00e-4ad2-8ddb-0d4e2b7a1cf0';
const API_BASE_URL = 'https://services.leadconnectorhq.com';
const LOCATION_ID = 'dONNCZt2WqGTCfexlC7S'; // Mandatory for search

// I set the start of today (00:00:00) to filter conversations
const todayStart = new Date();
todayStart.setHours(0, 0, 0, 0);

try {
  let validConversations = [];
  let stopFetching = false;
  let cursor = null; // For pagination (startAfterDate)

  // I loop to fetch pages until I hit old dates or finish
  while (!stopFetching) {
    // Construct URL with query parameters
    let url = `${API_BASE_URL}/conversations/search?locationId=${LOCATION_ID}&sort=desc&sortBy=last_message_date&limit=100&lastMessageType=TYPE_WHATSAPP`;

    if (cursor) {
      url += `&startAfterDate=${cursor}`;
    }

    const response = await fetch(url, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${GHL_ACCESS_TOKEN}`,
        'Version': '2021-04-15',
        'Accept': 'application/json'
      }
    });

    if (!response.ok) throw new Error(`API Error: ${response.statusText}`);

    const data = await response.json();
    const conversations = data.conversations || [];

    if (conversations.length === 0) break; // No more data

    // I iterate through the fetched batch to check dates
    for (const conv of conversations) {
      // Use lastMessageDate or dateAdded as fallback
      const convDate = new Date(conv.lastMessageDate || conv.dateAdded);

      if (convDate >= todayStart) {
        // It's from today, keep it
        validConversations.push({
          json: {
            id: conv.id,
            contactId: conv.contactId,
            fullName: conv.fullName,
            lastDate: convDate
          }
        });
      } else {
        // We reached yesterday's data. Since it's sorted DESC, we stop everything.
        stopFetching = true;
        break; 
      }
    }

    // Update cursor for next page (if we are continuing)
    if (!stopFetching && conversations.length > 0) {
      // GHL search uses the sort value of the last item for pagination
      cursor = conversations[conversations.length - 1].lastMessageDate;
    }
  }

  console.log(`Found ${validConversations.length} conversations for today.`);

  // Return the filtered list to the next node
  return validConversations;

} catch (error) {
  console.error(error);
  throw error;
}

