const GHL_ACCESS_TOKEN = 'pit-94f29fed-e00e-4ad2-8ddb-0d4e2b7a1cf0';
const API_BASE_URL = 'https://services.leadconnectorhq.com';
const BATCH_SIZE = 100; 

// I define the list of Conversation IDs to ignore (e.g., Team groups, Internal chats)
const IGNORED_CONVERSATION_IDS = [
  'vKVjGbNht0t7csjwtBhF', 
  '4Tr6HmtaFEP8YtqKOwah'
];

// Team & AI IDs for classification
const TEAM_USER_IDS = [
  'f0WIjoQupAe6m8CwYxqT', // AsemR
  'cD2cRozfGaY1dMBVPrQW', // Ahemd
  'ocpyLNdF0OVgDBFe9Ztd', // Mohammed 
  'pnIGhDkQCYHg469w8Ut6'  // Ghanou
];
const AI_AGENT_USER_ID = '5A7qBK1fknuDuOyznzfA';

// I get all input items from the previous node (Search Node)
const inputs = $input.all();

// Helper function: Fetch full history for ONE conversation
async function fetchFullHistory(conversationId, contactId) {
  let allMessages = [];
  let hasMorePages = true;
  let lastMessageId = null;

  try {
    while (hasMorePages) {
      let url = `${API_BASE_URL}/conversations/${conversationId}/messages?limit=100`;
      if (lastMessageId) url += `&lastMessageId=${lastMessageId}`;

      const res = await fetch(url, {
        headers: { 
          'Authorization': `Bearer ${GHL_ACCESS_TOKEN}`, 
          'Version': '2021-04-15' 
        }
      });

      if (!res.ok) throw new Error('Msg API Fail');
      const data = await res.json();
      const msgs = data.messages?.messages || [];

      if (msgs.length > 0) allMessages = allMessages.concat(msgs);

      hasMorePages = data.messages?.nextPage === true;
      lastMessageId = data.messages?.lastMessageId;

      if (hasMorePages && !lastMessageId) break; 
    }

    // Sort messages chronologically
    allMessages.sort((a, b) => new Date(a.dateAdded) - new Date(b.dateAdded));

    // Format to Markdown
    let markdown = "";
    let lastDirection = "";

    for (const msg of allMessages) {
      let sender = "Unknown";
      if (msg.direction === 'inbound') sender = "User";
      else if (msg.direction === 'outbound') {
        if (msg.userId === AI_AGENT_USER_ID) sender = "AI Agent";
        else if (TEAM_USER_IDS.includes(msg.userId) || !msg.userId) sender = "Team";
      }

      const body = msg.body || "[Media]";
      const date = new Date(msg.dateAdded).toISOString().split('T')[0];
      markdown += `[${sender} - ${date}]: ${body}\n`;
      lastDirection = msg.direction;
    }

    // I return the structure including the crucial Contact ID
    return {
      conversationId: conversationId,
      contactId: contactId, // <--- Preserved here
      history: markdown,
      messageCount: allMessages.length,
      lastDirection: lastDirection
    };

  } catch (e) {
    return { 
      conversationId: conversationId, 
      contactId: contactId, 
      error: e.message, 
      history: "Error fetching" 
    };
  }
}

// ----------------Main Execution Logic----------------
const processedConversations = [];
const CONCURRENCY = 5; 

// I filter the inputs first to remove ignored IDs
const validInputs = inputs.filter(item => {
  const id = item.json.id;
  // If the ID is in the ignored list, I skip it
  return !IGNORED_CONVERSATION_IDS.includes(id);
});

console.log(`Processing ${validInputs.length} conversations after exclusion.`);

for (let i = 0; i < validInputs.length; i += CONCURRENCY) {
  const chunk = validInputs.slice(i, i + CONCURRENCY);

  // I pass both ID and ContactID to the fetch function
  const promises = chunk.map(item => fetchFullHistory(item.json.id, item.json.contactId));

  const results = await Promise.all(promises);
  processedConversations.push(...results);
}

// ----------------Batching Logic----------------
const batches = {};
const numberOfBatches = Math.ceil(processedConversations.length / BATCH_SIZE);

for (let i = 0; i < numberOfBatches; i++) {
  const start = i * BATCH_SIZE;
  const end = start + BATCH_SIZE;
  batches[`batch_${i + 1}`] = processedConversations.slice(start, end);
}

// Final Return Structure
return {
  json: {
    stats: {
      originalCount: inputs.length,
      processedCount: processedConversations.length,
      numberOfBatches: numberOfBatches,
      batchSize: BATCH_SIZE
    },
    batches: batches
  }
};

