// --------------------------------------------------------
// 1. إعدادات الربط والثوابت (Configuration)
// --------------------------------------------------------
const AIRTABLE_API_KEY = 'patLu1tsO6OQv0sVe.9aa309808fc593eb51de73af9ea463a21c04b69566b8df75f49a06503ef6ba53'; 
const AIRTABLE_BASE_ID = 'appW9hLpui1gEztZF'; 

const TBL_COURSES = 'tbl39nQC6JONrHWe1';    
const TBL_INTERESTS = 'tbljj0lrbnXHNMjoR';  

const TYPE_FINANCE_ID = 'recrO4GO9ci1NdunR';   
const TYPE_INVESTMENT_ID = 'rec6FZInmRfNoSRRq'; 

const TOPIC_MAP = {
  'Personal Financial Management': TYPE_FINANCE_ID,
  'Special Financial Consultation': TYPE_FINANCE_ID, // افتراض: الاستشارة تتبع المالية (أو عدلها حسب رغبتك)
  'Investment and Wealth Management': TYPE_INVESTMENT_ID
};

// --------------------------------------------------------
