// هنا نعالج مشكلة اختلاف هيكلة البيانات
let aiResults = [];
const inputData = $input.first().json;

// نتأكد هل البيانات جاية داخل records (زي بياناتك الحالية)
if (inputData.records && Array.isArray(inputData.records)) {
  // نمشي على كل سجل ونستخرج البيانات ونرتبها
  aiResults = inputData.records.map(record => {
    const fields = record.fields || {};

    // 1. استخراج الـ ID
    const cId = fields.ContactId; // انتبه للحروف الكبيرة

    // 2. معالجة الاهتمامات (تحويل النص إلى مصفوفة)
    let interestsArray = [];
    if (fields.Interests && typeof fields.Interests === 'string') {
      try {
