  aiResults = $input.all().map(item => item.json);
}

// حماية: إذا لا توجد بيانات بعد المعالجة
if (!aiResults || aiResults.length === 0) {
  return { json: { result: "No valid input data found" } };
}

// --------------------------------------------------------
// 3. دوال مساعدة (Helper Functions)
// --------------------------------------------------------

async function fetchAllRecords(tableId, params = {}) {
  let allRecords = [];
  let offset = null;

  do {
    let url = `https://api.airtable.com/v0/${AIRTABLE_BASE_ID}/${tableId}?`;

    if (params.sort) url += `&sort[0][field]=${params.sort.field}&sort[0][direction]=${params.sort.direction}`;
    if (params.fields) params.fields.forEach(f => url += `&fields[]=${f}`);
    if (params.filterByFormula) url += `&filterByFormula=${encodeURIComponent(params.filterByFormula)}`;
    if (offset) url += `&offset=${offset}`;

    const response = await fetch(url, {
      headers: { 'Authorization': `Bearer ${AIRTABLE_API_KEY}` }
    });

    if (!response.ok) throw new Error(`Airtable Error (${tableId}): ${response.statusText}`);

    const data = await response.json();
    allRecords = allRecords.concat(data.records);
    offset = data.offset; 

  } while (offset);

  return allRecords;
}

async function performBatchOp(tableId, method, records) {
  const CHUNK_SIZE = 10; 
  const results = [];

  for (let i = 0; i < records.length; i += CHUNK_SIZE) {
    const chunk = records.slice(i, i + CHUNK_SIZE);

    const response = await fetch(`https://api.airtable.com/v0/${AIRTABLE_BASE_ID}/${tableId}`, {
      method: method, 
      headers: {
        'Authorization': `Bearer ${AIRTABLE_API_KEY}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ records: chunk, typecast: true })
    });

    if (!response.ok) {
      const err = await response.text();
      console.error(`Batch Op Failed: ${err}`);
      continue; 
    }

    const data = await response.json();
    results.push(...data.records);
  }
  return results;
}

// --------------------------------------------------------
// 4. المنطق الرئيسي (Core Logic)
// --------------------------------------------------------

// الخطوة أ: جلب الدورات وتحديد "الأحدث"
const rawCourses = await fetchAllRecords(TBL_COURSES, {
  sort: { field: 'Date', direction: 'desc' }, 
  fields: ['Course Type', 'Status', 'Date']
});

const latestInstances = {
  [TYPE_FINANCE_ID]: null,
  [TYPE_INVESTMENT_ID]: null
};

const instanceTypeMap = {}; 

for (const record of rawCourses) {
  const courseTypeId = record.fields['Course Type']?.[0]; 
  const id = record.id;
  const status = record.fields['Status'];

  if (courseTypeId) instanceTypeMap[id] = courseTypeId;

  if (courseTypeId && !latestInstances[courseTypeId]) {
    latestInstances[courseTypeId] = { id: id, status: status };
  }

  if (latestInstances[TYPE_FINANCE_ID] && latestInstances[TYPE_INVESTMENT_ID]) break;
}

// الخطوة ب: جلب اهتمامات العملاء الحالية
const contactIds = aiResults.map(r => r.contactId).filter(id => id);

let contactInterestsMap = {};

// نجلب البيانات فقط إذا فيه عملاء
if (contactIds.length > 0) {
    const formula = `OR(${contactIds.map(id => `SEARCH('${id}', ARRAYJOIN({Contact}))`).join(',')})`;

    const rawInterests = await fetchAllRecords(TBL_INTERESTS, {
      fields: ['Contact', 'Source Instance', 'Reason'],
      filterByFormula: formula
    });

    for (const record of rawInterests) {
      const cId = record.fields['Contact']?.[0]; 
      const instanceId = record.fields['Source Instance']?.[0];

      if (cId && instanceId) {
        if (!contactInterestsMap[cId]) contactInterestsMap[cId] = [];

        const type = instanceTypeMap[instanceId];

        contactInterestsMap[cId].push({
          recordId: record.id,
          instanceId: instanceId,
          type: type 
        });
      }
    }
}

// الخطوة ج: غرفة العمليات
const toCreate = [];
const toUpdate = [];

for (const customer of aiResults) {
  const cId = customer.contactId;

  // الآن نستخدم الحماية ? لكن البيانات يفترض تكون نظيفة من مرحلة التجهيز
  const interests = customer.classification?.interests || [];

  for (const interest of interests) {

    const topicType = TOPIC_MAP[interest.topic]; 
    if (!topicType) continue; 

    const targetInstance = latestInstances[topicType];
    if (!targetInstance) continue; 

    let reason = "Didn't Signup"; 
    if (interest.time === 'new') {
      if (targetInstance.status !== 'Enrollment Open') {
        reason = 'Not Open';
      }
    }

    const existingRecords = contactInterestsMap[cId] || [];
    const existingRecord = existingRecords.find(r => r.type === topicType);

    if (interest.time === 'new') {
      // --- حالة NEW ---
      if (existingRecord) {
        if (existingRecord.instanceId !== targetInstance.id) {
          toUpdate.push({
            id: existingRecord.recordId,
            fields: {
              'Source Instance': [targetInstance.id], 
              'Reason': reason
            }
          });
        }
      } else {
        toCreate.push({
          fields: {
            'Contact': [cId],
            'Source Instance': [targetInstance.id],
            'Reason': reason
          }
        });
      }

    } else if (interest.time === 'old') {
      // --- حالة OLD ---
      if (!existingRecord) {
        toCreate.push({
          fields: {
            'Contact': [cId],
            'Source Instance': [targetInstance.id], 
            'Reason': "Didn't Signup"
          }
        });
      }
    }
  }
}

// الخطوة د: التنفيذ
let createdCount = 0;
let updatedCount = 0;

if (toCreate.length > 0) {
  const res = await performBatchOp(TBL_INTERESTS, 'POST', toCreate);
  createdCount = res.length;
}

if (toUpdate.length > 0) {
  const res = await performBatchOp(TBL_INTERESTS, 'PATCH', toUpdate);
  updatedCount = res.length;
}

// -----------------------------------------------------
// 5. التقرير النهائي
// -----------------------------------------------------
return {
  json: {
    status: "success",
    summary: {
      total_processed_customers: aiResults.length,
      records_created: createdCount,
      records_updated: updatedCount
    },
    operations_log: {
      created: toCreate.length,
      updated: toUpdate.length
    }
  }
};

