async function syncContactTags(contactId, newTag) {
  const result = { contactId, removed: false, added: null, errors: [] };

  const headers = {
    'Authorization': `Bearer ${GHL_ACCESS_TOKEN}`,
    'Version': '2021-07-28',
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  };

  // STEP A: REMOVE OLD TAGS
  try {
    const deleteUrl = `${API_BASE_URL}/contacts/${contactId}/tags`;
    const deleteRes = await fetch(deleteUrl, {
      method: 'DELETE',
      headers: headers,
      body: JSON.stringify({ tags: TAGS_TO_REMOVE })
    });

    if (deleteRes.ok) {
      result.removed = true;
    } else {
      // const errText = await deleteRes.text();
      // warning ignored
    }
  } catch (err) {
    result.errors.push(`Delete Network Error: ${err.message}`);
  }

  // STEP B: ADD NEW TAG
  if (newTag && newTag !== 'General') {
    try {
      const addUrl = `${API_BASE_URL}/contacts/${contactId}/tags`;
      const addRes = await fetch(addUrl, {
        method: 'POST',
        headers: headers,
        body: JSON.stringify({ tags: [newTag] })
      });

      if (addRes.ok) {
        result.added = newTag;
      } else {
        const errText = await addRes.text();
        result.errors.push(`Add Failed: ${errText}`);
      }
    } catch (err) {
      result.errors.push(`Add Network Error: ${err.message}`);
    }
  }

  return result;
}

