for (let i = 0; i < inputs.length; i += CONCURRENCY_LIMIT) {
  const chunk = inputs.slice(i, i + CONCURRENCY_LIMIT);

  const promises = chunk.map(item => {
    return syncContactTags(item.contactId, item.classification.tag);
  });

  const chunkResults = await Promise.all(promises);
  finalResults.push(...chunkResults);
}

return {
  json: {
    status: "success",
    processed_count: finalResults.length, // المفروض يطلع 7 الآن
    details: finalResults
  }
