// دالة لجلب رقم الهاتف من GHL
async function getContactPhone(contactId) {
  if (!contactId) return null; // حماية إضافية
  try {
    const url = `${GHL_BASE_URL}/contacts/${contactId}`;
    const res = await fetch(url, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${GHL_ACCESS_TOKEN}`,
        'Version': '2021-07-28',
        'Accept': 'application/json'
      }
    });

    if (!res.ok) return null;

    const data = await res.json();

    // التعامل مع رد GHL سواء كان Array أو Object
    // حسب المثال اللي أرسلته أنت، الرد هو مصفوفة [ { contact: { ... } } ]
    const contactObj = Array.isArray(data) ? data[0]?.contact : (data.contact || data);

    return contactObj?.phone || null;
  } catch (e) {
    return null;
  }
}

// دالة لجلب العلامات الحالية من Waha
async function getWahaLabels(chatId) {
  try {
    const url = `${WAHA_BASE_URL}/api/${SESSION_NAME}/labels/chats/${chatId}`;
    const res = await fetch(url);
    if (!res.ok) return [];

    const data = await res.json();
    return data.map(l => l.id) || [];
  } catch (e) {
    return [];
  }
}

// الدالة الرئيسية لمعالجة كل عميل
async function processConversation(item) {
  const result = { 
    contactId: item.contactId, 
    status: 'skipped', 
    reason: null,
    labels_set: []
  };

  // أ. جلب رقم الهاتف
  const rawPhone = await getContactPhone(item.contactId);

  if (!rawPhone) {
    result.status = 'failed';
    result.reason = `Phone not found for ContactID: ${item.contactId}`;
    return result;
  }

  // ب. تحويل الرقم لصيغة Waha
  const cleanPhone = rawPhone.replace(/\D/g, ''); 
  const wahaChatId = `${cleanPhone}@c.us`;

  // ج. تحديد العلامات الجديدة من الـ AI
  const aiLabels = [];
  const classification = item.classification;

  if (classification.tag === 'Companies') aiLabels.push(LABELS.COMPANIES);
  if (classification.tag === 'Appointment') aiLabels.push(LABELS.CONSULTING);

  if (classification.interests) {
    for (const interest of classification.interests) {
      if (interest.topic === 'Investment and Wealth Management') aiLabels.push(LABELS.MARKETING_INVEST);
      if (interest.topic === 'Personal Financial Management') aiLabels.push(LABELS.MARKETING_SAVINGS);
    }
  }

  let finalLabels = [...new Set(aiLabels)];

  // د. المنطق الهجين
  if (classification.tag === 'Companies') {
    const currentLabels = await getWahaLabels(wahaChatId);
    finalLabels = [...new Set([...currentLabels, ...finalLabels])];
    result.logic = 'merge_existing';
  } else {
    result.logic = 'overwrite';
  }

  // هـ. تطبيق التغييرات في Waha
  try {
    const url = `${WAHA_BASE_URL}/api/${SESSION_NAME}/labels/chats/${wahaChatId}/`;
    // Payload Format: { "labels": [ {"id": "58"}, {"id": "237"} ] }
    const body = { labels: finalLabels.map(id => ({ id })) };

    const res = await fetch(url, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json', 'X-Api-Key': '9f5e7c47a2d892adf840cb21c227c07e349f2bbec9c5c99b147b0c8c71668a4f' },
      body: JSON.stringify(body)
    });

    if (res.ok) {
      result.status = 'success';
      result.labels_set = finalLabels;
      result.wahaChatId = wahaChatId;
    } else {
      const err = await res.text();
      result.status = 'error';
      result.reason = `Waha API Error: ${err}`;
    }
